import React, { useState } from 'react';
import axios from 'axios';

const SentimentForm = () => {
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const analyzeSentiment = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:8000/analyze_single', { text });
      setResult(response.data);
    } catch (error) {
      alert('API Error: ' + error.message);
    }
    setLoading(false);
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4 text-center">ðŸ§  Sentiwave - Real-Time Sentiment Analyzer</h1>
      <form onSubmit={analyzeSentiment} className="space-y-4">
        <textarea
          className="w-full p-2 border rounded"
          rows={4}
          placeholder="Type your sentence here..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          disabled={loading}
        >
          {loading ? "Analyzing..." : "Analyze Sentiment"}
        </button>
      </form>

      {result && (
        <div className="mt-6 p-4 bg-gray-100 rounded shadow">
          <p><strong>Sentiment:</strong> {result.label}</p>
          <p><strong>Positive:</strong> {result.pos.toFixed(3)}</p>
          <p><strong>Negative:</strong> {result.neg.toFixed(3)}</p>
        </div>
      )}
    </div>
  );
};

export default SentimentForm;
