import React, { useState } from 'react';
import axios from 'axios';

const BatchSentimentForm = () => {
  const [textBlock, setTextBlock] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const analyzeBatch = async (e) => {
    e.preventDefault();
    const lines = textBlock
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.length > 0);

    if (lines.length === 0) return alert("Please enter at least one sentence.");

    setLoading(true);
    try {
      const response = await axios.post('http://localhost:8000/analyze_batch', {
        texts: lines
      });
      setResults(response.data);
    } catch (err) {
      alert("Batch API Error: " + err.message);
    }
    setLoading(false);
  };

  return (
    <div className="max-w-2xl mx-auto p-4 mt-8 border-t pt-6">
      <h2 className="text-xl font-semibold mb-3">üîÅ Batch Sentiment Analysis</h2>
      <form onSubmit={analyzeBatch} className="space-y-4">
        <textarea
          className="w-full p-2 border rounded"
          rows={6}
          placeholder="Enter one sentence per line..."
          value={textBlock}
          onChange={(e) => setTextBlock(e.target.value)}
        />
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          disabled={loading}
        >
          {loading ? "Analyzing..." : "Analyze Batch"}
        </button>
      </form>

      {results.length > 0 && (
        <div className="mt-6 space-y-2">
          {results.map((res, i) => (
            <div key={i} className="p-3 bg-gray-50 border rounded">
              <p><strong>Line {i + 1}</strong> ‚Äî <span className="font-bold">{res.label}</span></p>
              <p className="text-sm text-gray-600">Pos: {res.pos.toFixed(3)}, Neg: {res.neg.toFixed(3)}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BatchSentimentForm;
